#include <Python.h>// 建立費氏數列運算函數int fibonacci(int n){	if (n < 2)		return n;	else		return fibonacci(n-1) + fibonacci(n-2);};static PyObject* fib(PyObject* self, PyObject* args){	int n;	if(!PyArg_ParseTuple(args, "i", &n))		return NULL;		// 當Python使用fib()函數時這裡將呼叫fibonacci(n)函數進行計算.	return Py_BuildValue("i", fibonacci(n));};static PyMethodDef fibonacciMethod[] = {	{"fib", fib, METH_VARARGS, "費氏數列計算"},	// PyMethodDef結束一定要使用 {NULL, NULL, 0, NULL}	{NULL, NULL, 0, NULL}};PyMODINIT_FUNCinitfib(void){	(void) Py_InitModule("fib", fibonacciMethod);};